import React, { useState } from 'react';
import { CgArrowLongDownR } from 'react-icons/cg';
import './App.css';
var axios = require("axios");

const App = () => {
  const [lat, setLat] = useState(null);
  const [lng, setLng] = useState(null);
  const [status, setStatus] = useState(null);
  const [MBTALine, setMBTALine] = useState(null);
  const [errorCode, setErrorCode] = useState(null);

  const getLocation = () => {
    if (!navigator.geolocation) {
      setStatus('Geolocation is not supported by your browser');
    } else {
      navigator.geolocation.getCurrentPosition((position) => {
        setStatus(null);
        setLat(position.coords.latitude);
        setLng(position.coords.longitude);
      }, () => {
        setStatus('Unable to retrieve your location');
        var pic = document.getElementById("response")
        pic.innerHTML = '<img class="sad-response" src="sad-face.png"/>'
      });
    }
  }

  const getLineData = () => {
    const body = JSON.stringify({"line": "Green-B"});
    console.log(body)
    axios({
      method: "get",
      url: "http://127.0.0.1:5000/get_trains_by_line",
      data: body,
      headers: {
        'Content-Type': 'application/json'
      }
    })
    .then(function (response) {
      setMBTALine(JSON.stringify(response.data))
      console.log(MBTALine)
    })
    .then(console.log("Success"))
  }

  return (
    <div className="App-header">
      <div className="App">
        <div id="response"> <CgArrowLongDownR className="arrowDown" /> </div>
        <button onClick={getLocation}>get location</button>
        <p>{status}</p>
        <button onClick={getLineData}>Get MBTA Line Data</button>
        {lat && <p>Latitude: {lat}</p>}
        {lng && <p>Longitude: {lng}</p>}
        {MBTALine && <p>{MBTALine}</p>}
      </div>
    </div>
  );
}

export default App;
